function validate(output){

    var canEnter = document.getElementById('string').textContent;

    if(canEnter.trim(' ') != ''){

        if(document.getElementById('stringEmpty').textContent.trim(' ') != ''){
            document.getElementById('stringEmpty').innerHTML = '';
        }

        if(document.getElementById('error').textContent.trim(' ') != ''){
            document.getElementById('error').innerHTML = '';
        }

        if(output){
            document.getElementById('cleanEntryButton').style.display = 'flex';
        }

        return true;
    }
    
    else{
        document.getElementById('stringEmpty').innerHTML = 'String vazia!';
        return false;
    }
    
}

function send(){

    var string = document.getElementById('inputString');
    document.getElementById('string').innerHTML = string.value;
    document.getElementById('inputString').value = '';
    
    if(!validate()){
        document.getElementById('stringEmpty').innerHTML = '';
        document.getElementById('error').innerHTML = 'String inv√°lida!'
    }
    else{
        document.getElementById('buttons').style.display = 'flex';
        document.getElementById('message').style.display = 'flex';
    }
}

function clean(){

    document.getElementById('string').innerHTML = '';
    document.getElementById('buttons').style.display = 'none';
    document.getElementById('message').style.display = 'none';

    cleanEntry();

}

function cleanEntry(){

    document.getElementById('stringUpper').innerHTML = '';
    document.getElementById('stringProper').innerHTML = '';
    document.getElementById('stringCapital').innerHTML = '';
    document.getElementById('stringLower').innerHTML = '';
    document.getElementById('stringMd5').innerHTML = '';
    document.getElementById('stringBase64').innerHTML = '';
    document.getElementById('stringDecodeBase64').innerHTML = '';
    document.getElementById('cleanEntryButton').style.display = 'none';

}

